mapper.select:
SELECT 

FROM goods a

WHERE a.id = #{id}

SELECT 

FROM goods a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM goods a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE goods SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
