mapper.select:
SELECT 

FROM orders a

WHERE a.id = #{id}

SELECT 

FROM orders a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.order_id = #{orderId}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT ,a.goods_count*a.goods_price total,a.goods_count*a.vip_price vipTotal,g.goods_name goodsName FROM orders a,goods g
a.del_flag = #{DEL_FLAG_NORMAL} and a.login_name = #{loginName} and a.goods_id = g.id
AND a.order_id = #{orderId}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT ,g.goods_name goodsName FROM orders a,goods g
a.del_flag = #{DEL_FLAG_NORMAL} and a.goods_id = g.id
AND a.order_id = #{orderId}
ORDER BY ${page.orderBy}
ORDER BY a.status aSC

SELECT 

FROM orders a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE orders SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
