mapper.select:
SELECT 

FROM pv_detail a

WHERE a.id = #{id}

select * from pv_detail
where DATE_FORMAT(create_date,'%Y-%m-%d') = #{pvDate}


        and login_Name = #{loginName}


ORDER BY create_date DESC

select create_date, sum(pvTotal1) pvTotal1,sum(pvTotal2) pvTotal2,sum(pvTotal3) pvTotal3, sum(pvSheng1) pvSheng1,sum(pvSheng2) pvSheng2,sum(pvSheng3) pvSheng3, sum(pvDues1) pvDues1,sum(pvDues2) pvDues2,sum(pvDues3) pvDues3 from( select DATE_FORMAT(create_date,'%Y-%m-%d') create_date, case when pvType='1' then pvTotal else 0 end pvTotal1, case when pvType='1' then pvSheng else 0 end pvSheng1, case when pvType='1' then pvDues else 0 end pvDues1, case when pvType='2' then pvTotal else 0 end pvTotal2, case when pvType='2' then pvSheng else 0 end pvSheng2, case when pvType='2' then pvDues else 0 end pvDues2, case when pvType='3' then pvTotal else 0 end pvTotal3, case when pvType='3' then pvSheng else 0 end pvSheng3, case when pvType='3' then pvDues else 0 end pvDues3 from pv_detail
where login_name = #{loginName}
group by DATE_FORMAT(create_date,'%Y-%m-%d'),pvType )a group by create_date
ORDER BY ${page.orderBy}
ORDER BY a.create_date DESC

SELECT 

FROM pv_detail a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE pv_detail SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
