mapper.select:
SELECT 

FROM zhou_bonus a

WHERE a.id = #{id}

SELECT 

FROM zhou_bonus a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM zhou_bonus a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
